import React from "react";

const Hero = () => {
    return (
        <section className = "hero is-success">
            <div className = "hero-body">
                <div className = "container">
                    <h1 className = "title">初めてのページ作り</h1>
                    <h2 className = "subtitle">bulmaって凄い。</h2>
                </div>
            </div>
        </section>
    );
};

const Message = () => {
    return (
        <article class="message">
            <div class="message-header">
                <p>こんにちわ。</p>
                <button class="delete is-medium" aria-label="delete"></button>
            </div>
            <div class="message-body">
                 このページは<strong>練習用のページ</strong>です。
            </div>
        </article>
    );
}

const Menu = () => {
    return (
        <aside class="menu">
            <p class="menu-label">メニュー</p>
            <ul class="menu-list">
                <li><a>試した その１</a></li>
                <li><a>試した その２</a></li>
                <li><a>試した その３</a></li>
            </ul>
        </aside>
    );
}

const Body1 = () => {
    return (
    <section className = "section is-dark">
        <div className = "container">
            <h2 className = "subtitle">ボタンの表示をしてみました。</h2>
            <p>あなたの性別であるボタンをクリックしてください。</p>
            <button class = "button is-medium">男</button>
            <button class = "button is-medium">女</button>
            <button class = "button is-medium">その他</button>
        </div>
    </section>
    );
}

const Body2 = () => {
    return (
    <section className = "section">
        <div className = "container">
            <h2 className = "subtitle">選択式の表示をしてみました。</h2>
            <p>あなたの性別を選んでください。</p> 
            <div class="select">
                <select>
                    <option>男性</option>
                    <option>女性</option>
                    <option>その他</option>
                </select>
            </div>
        </div>
    </section>
    );
}

const Body3 = () => {
    return (
    <section className = "section">
        <div className = "container">
            <h2 className = "subtitle">テキストエリアの表示をしてみました。</h2>
            <p>あなたの性別を記入してください。</p>
            <textarea class="textarea" placeholder="男、女、その他"></textarea>
        </div>
    </section>
    );
}

const Footer = () => {
    return (
        <footer className = "footer">
            <dvi className = "content has-text-centered">
                <p>&copy; 2020 miura</p>
            </dvi>
        </footer>
    );
}

const App = () => {
    return (
        <div>
            <Message />
            <Hero />
            <Menu />
            <Body1 />
            <Body2 />
            <Body3 />
            <Footer />
        </div>
    );
};

export default App;
